options.script:
// Helper function to check if field has values
function hasValues(field) {
    return field && field.values && field.values.length > 0;
}

// Helper function to get field values
function getValues(field) {
    return field.values || field.values.buffer;
}

// Add viridis color mapping function
function getViridisColor(value, min, max) {
    const viridisColors = [
        '#440154', '#443982', '#31688e', '#21918c', '#35b779', '#90d743', '#fde725'
    ];
    const normalizedValue = (value - min) / (max - min);
    const index = Math.floor(normalizedValue * (viridisColors.length - 1));
    return viridisColors[Math.min(viridisColors.length - 1, Math.max(0, index))];
}

let traces = [];
let shapes_verify = [];
let shapes_fcst = [];
let shapes_ltn = [];   
const rain_primary = data.series[0];
const rain_secondary = data.series[1];
const truth_onset = data.series[2];
const forecast_onset = data.series[3];
const ltn_onset = data.series[4];

const year = Number(variables.verify_year.current.value);
const yearStart = new Date(year, 0, 1).getTime();
const yearEnd = new Date(year, 12, 31).getTime();
const show_compare = Boolean(variables.compare.current.value == "True");

// Get time range for threshold lines (if time series exists)
let timeRange = [];
if (hasValues(rain_primary.fields[0])) {
    const timeValues = getValues(rain_primary.fields[0]);
    timeRange = [Math.min(...timeValues), Math.max(...timeValues)];
}

// Define trace configurations for precipitation data
const traceConfigs = [
{
    data: rain_primary.fields[1],  // daily precip
    name: "Source 1: Avg. 2m Temp",
    color: '#73A580',
    mode: 'markers',
    marker: {
        size: 8  
    },
},
{
    data: rain_secondary.fields[1],  // daily precip
    name: "Source 2: Avg. 2m Temp",
    color: '#F4A261',
    mode: 'line'
}
];


// Add precipitation traces if they have data
traceConfigs.forEach(config => {
    if (hasValues(config.data)) {
        traces.push({
            x: getValues(rain_primary.fields[0]),
            y: getValues(config.data),
            type: 'scatter',
            mode: config.mode,
            marker: {
                size: 4 
            },            
            name: config.name,
            line: { color: config.color }
        });
    }
});

// Add threshold lines if we have a time range
if (timeRange.length) {
    const thresholds = [
        { value: 10, name: 'Cap 35', color: '#118AB2', dash: 'dash' },
        { value: 35, name: 'Floor 10', color: '#EF476F', dash: 'dashdot' }
    ];

    thresholds.forEach(threshold => {
        traces.push({
            x: timeRange,
            y: [threshold.value, threshold.value],
            type: 'scatter',
            mode: 'lines',
            name: threshold.name,
            line: {
                color: threshold.color,
                width: 2,
                dash: threshold.dash
            }
        });
    });
}
    // Process forecast onset dates with leads (converting seconds to days)
if (hasValues(forecast_onset?.fields[0])) {
    const leadValues = getValues(forecast_onset.fields[1]).map(lead => lead);  // Convert seconds to days
    const onsetDates = getValues(forecast_onset.fields[2]);  // forecast onset dates
    const uniqueLeads = [...new Set(leadValues)].sort((a, b) => a - b);

    uniqueLeads.forEach(lead => {
        const leadIndices = leadValues.map((v, i) => v === lead ? i : -1).filter(i => i !== -1);
        const leadTimes = leadIndices.map(i => onsetDates[i]);  // Use onset dates instead of issue times
        const color = getViridisColor(lead, Math.min(...uniqueLeads), Math.max(...uniqueLeads));

        traces.push({
            x: leadTimes,
            y: Array(leadTimes.length).fill(0),
            type: 'scatter',
            mode: 'markers',
            name: `Forecast Lead ${lead+1}`,
            marker: {
                symbol: 'x',
                color: color,
                size: 10
            }
        });

        shapes_fcst =  shapes_fcst.concat(
            leadTimes.map(time => ({
                type: 'line',
                x0: time,
                x1: time,
                y0: 0,
                y1: 1,
                xref: 'x',
                yref: 'paper',
                line: {
                    color: color,
                    width: 1,
                    dash: 'solid'
                }
            }))
        );
    });
}


// Process onset dates
const onsets = truth_onset?.fields[0];
if (hasValues(onsets)) {
    const onsetDates = getValues(onsets).map(timestamp => new Date(timestamp));

    // Separate verification year dates and historical dates
    const dates_verify = [];
    const historicalDates = [];

    onsetDates.forEach(date => {
        if (date.getFullYear() === year) {
            dates_verify.push(date);
        } else {
            const normalizedDate = new Date(year, date.getMonth(), date.getDate());
            historicalDates.push(normalizedDate);
        }
    });

    // Add historical onset markers (black)
    if (historicalDates.length > 0  && false ) {
        traces.push({
            x: historicalDates,
            y: Array(historicalDates.length).fill(0),
            type: 'scatter',
            mode: 'markers',
            name: 'Historical Onsets',
            marker: {
                color: 'black',
                symbol: 'x',
                size: 10
            }
        });
    }

    // Add verify year onset markers (red)
    if (dates_verify.length > 0) {
        traces.push({
            x: dates_verify,
            y: Array(dates_verify.length).fill(0),
            type: 'scatter',
            mode: 'markers',
            name: `${year} Onsets`,
            marker: {
                color: 'red',
                symbol: 'x',
                size: 10
            }
        });
    }

    // Create vertical lines for verify dates (red)
    shapes_verify = dates_verify.map(date => ({
        type: 'line',
        x0: date,
        x1: date,
        y0: 0,
        y1: 1,
        xref: 'x',
        yref: 'paper',
        line: {
            color: 'red',
            width: 1,
            dash: 'solid'
        }
    }));
}

// Process ltn onset dates
const ltn_onsets = ltn_onset?.fields[0];
if (hasValues(ltn_onsets) && false) {
    const ltnOnsetDates = getValues(ltn_onsets).map(timestamp => new Date(timestamp));

    const ltnDates = [];

    ltnOnsetDates.forEach(date => {
        const ltnDate = new Date(year, date.getMonth(), date.getDate());
        ltnDates.push(ltnDate);
    });

    if (ltnDates.length > 0) {
        traces.push({
            x: ltnDates,
            y: Array(ltnDates.length).fill(0),
            type: 'scatter',
            mode: 'markers',
            name: '2004-2015 LTN Onset',
            marker: {
                color: 'gold',
                symbol: 'x',
                size: 10
            }
        });
    }

    shapes_ltn = ltnDates.map(date => ({
        type: 'line',
        x0: date,
        x1: date,
        y0: 0,
        y1: 1,
        xref: 'x',
        yref: 'paper',
        line: {
            color: 'gold',
            width: 1,
            dash: 'solid'
        }
    }));
}

return {
    data: traces,
    layout: {
        shapes: [...shapes_verify],
        // shapes: [...shapes_fcst, ...shapes_verify],
        xaxis: {
            range: [yearStart, yearEnd],
            type: 'date',
            title: 'Time'
        },        
        yaxis: { 
            title: 'Precipitation (mm)'
        },
        showlegend: true
    }
};

